import React, { Component } from 'react'
import './HelperFile'
import './CoinContStyles.css'
import { choice } from './HelperFile'

export default class CoinCont extends Component {
    static defaultProps = {
        coins: [
            { side: "heads", imgUrl: "https://tinyurl.com/react-coin-heads-jpg"},
            { side: "tails", imgUrl: "https://tinyurl.com/react-coin-tails-jpg"}
        ]

    }
    constructor(props) {
        super(props)
        this.state = {
            numFlips: 0,
            numHeads: 0,
            numTails: 0,
            currCoin: null
        }

    }

    flipCoin = () => {
        const newCoin = choice(this.props.coins);
        this.setState(curState =>  {
            return {
                currCoin: newCoin,
                numFlips: curState.numFlips + 1
            }
        })
    }

    handleChange = () => {
        this.flipCoin()
    }
  render() {
    return (
      <div className='coincont'>
        <h1>Flip the Coin!</h1>
        <button onClick={()=> this.handleChange()}>Flip Coin</button>
        <p>Out of {this.state.numFlips} flips, there have been {this.state.numHeads}{" "} 
        heads and {this.state.numTails} tails.</p>
      </div>
    )
  }
}
